import React, { useState } from 'react';

const JournalSubmissionForm = () => {
  const [keywords, setKeywords] = useState([]);
  const [authorRows, setAuthorRows] = useState([{}]);

  const addAuthorRow = () => {
    setAuthorRows([...authorRows, {}]);
  };

  const removeAuthorRow = (indexToRemove) => {
    setAuthorRows(authorRows.filter((_, index) => index !== indexToRemove));
  };

  const handleKeywordInput = (event) => {
    const tagValue = event.target.value.trim();

    if (event.key === 'Enter' || event.key === ',') {
      event.preventDefault();

      if (tagValue && keywords.length < 6) {
        if (!keywords.includes(tagValue.toLowerCase())) {
          setKeywords([...keywords, tagValue]);
        }

        event.target.value = '';
      }
    }
  };

  const removeKeyword = (keywordToRemove) => {
    setKeywords(keywords.filter((keyword) => keyword !== keywordToRemove));
  };

  return (
    <div className="d-flex justify-content-center align-items-center">
      <form>
        {/* ... Other form elements ... */}
        <div className="row mb-3">
          <label htmlFor="input-keywords" className="col-sm-2 col-form-label fw-bold text-white">
            Keywords
          </label>
          <div className="col-sm-8">
            <input
              type="text"
              name="journal-keywords"
              className="form-control"
              id="input-keywords"
              spellCheck={false}
              placeholder="Enter or add a comma after each keyword..."
              onKeyDown={handleKeywordInput}
            />
            {/* Show when reached the max keywords */}
            {keywords.length === 6 && <p id="keywords-warning">Maximum keywords reached!</p>}
            {/* Contains the keywords */}
            <div className="tag-container" id="tagContainer">
              {keywords.map((keyword, index) => (
                <div key={index} className="btn btn-secondary me-2 mt-2">
                  <span>{keyword}</span>
                  <span className="ms-2" onClick={() => removeKeyword(keyword)}>
                    <i className="fa-solid fa-xmark"></i>
                  </span>
                </div>
              ))}
            </div>
          </div>
          <div className="key-counts col-sm-2 fw-bold mt-2 text-white">
            <span id="key-numb">{6 - keywords.length}</span>&nbsp;remaining.
          </div>
        </div>
        {/* ... Other form elements ... */}
      </form>
    </div>
  );
};

export default JournalSubmissionForm;
